import React, { useEffect,useState } from 'react';
import Layaout from '../../parcials/Layaout';
import CardHeader from '../../../components/CardHeader'
import { getTicketsSystemTransfer, getStore } from '../../../functions/ticketFunction';
import Select from 'react-select';
import {
    MDBRow,
    MDBCol,
    MDBInput,
    MDBBtn,
    MDBIcon,
    MDBAnimation
} from 'mdbreact';

const TransferSystemPage = () => {
    const [dataStores, setdataSores] = useState([]);
    const [fields, setFields] = useState([{ upc: null,alu: null }]);
    let datos = [];

    useEffect(() => {
    getStore().then((res)=>{ _data(res)});
    },[]);


   let _data = (stores) =>{
        stores.map(res=> datos.push({value:res.name, label:res.name})) 
    }

    //created input
    function handleChange(i, event) {
        const values = [...fields];
        values[i].value = event.target.value;
        setFields(values);
      }

      function handleAdd() {
        const values = [...fields];
        values.push({ upc: null,alu: null });
        setFields(values);
      }

      function handleRemove(i) {
        const values = [...fields];
        values.splice(i, 1);
        setFields(values);
      }

      console.log(fields);

    const value = { value: 'Selecciona una tienda', label: 'Selecciona una tienda' };
    return (
        <Layaout>
            <br></br>
            <CardHeader title="Tickets" icon="ticket-alt">
            {fields.map((field, idx) => {
                return (
                <MDBRow style={{ justifyContent: "center", display: "flex" }} key={`${field}-${idx}`}>
                    <MDBCol md='2'>
                        <MDBInput label='Upc' type='text' validate />
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Alu' type='text' validate />
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Talla' type='text' validate />
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Factura' type='text' validate />
                    </MDBCol>
                    <MDBCol md='3' style={{marginTop: "26px"}}>
                        <Select
                            defaultValue={value}
                            options={datos}
                        />
                    </MDBCol>
                    <MDBCol md='1'>
                        <MDBBtn size="sm" color='danger'>X</MDBBtn>
                    </MDBCol>
                </MDBRow>
                )})}
                <MDBRow style={{ justifyContent: "center", display: "flex" }}>
                    <MDBBtn color='light-blue' onClick={() => handleAdd()}><MDBIcon icon="plus"/> Agregar</MDBBtn>
                    <MDBBtn color='light-green'><MDBIcon icon='ticket-alt'/>  Crear Ticket</MDBBtn>
                </MDBRow>
            </CardHeader>
        </Layaout>
    )
}

export default TransferSystemPage;
