import React, { useEffect,useState } from 'react';
import Layaout from '../../parcials/Layaout';
import CardHeader from '../../../components/CardHeader'
import { getTicketsSystemTransfer, getStore } from '../../../functions/ticketFunction';
import Select from 'react-select';
import {
    MDBRow,
    MDBCol,
    MDBInput,
    MDBBtn,
    MDBIcon,
    MDBAnimation
} from 'mdbreact';

const TransferSystemPage = () => {
    const [dataStores, setdataSores] = useState([]);
    const [fields, setFields] = useState([{ upc: null,alu: null }]);
    let datos = [];

    useEffect(() => {
    getStore().then((res)=>{ _data(res)});
    },[]);


   let _data = (stores) =>{
        stores.map(res=> datos.push({value:res.name, label:res.name})) 
    }

    //created input
    function handleChange(i, event) {
        console.log(event.target.name)
        let inputs = event.target.value;
        const values = [...fields];
        values[i].inputs = event.target.value;
        setFields({values});
      }

      function handleAdd() {
        const values = [...fields];
        values.push({ upc: null,alu: null });
        setFields(values);
      }

      function handleRemove(i) {
        const values = [...fields];
        values.splice(i, 1);
        setFields(values);
      }

      console.log(fields);

    const value = { value: 'Selecciona una tienda', label: 'Selecciona una tienda' };
    return (
        <div className="App">
        <h1>Hello CodeSandbox</h1>
  
        <button type="button" onClick={() => handleAdd()}>
          +
        </button>
  
        {fields.map((field, idx) => {
          return (
            <div key={`${field}-${idx}`}>
              <input
                type="text"
                placeholder="Enter text"
                name="upc"
                onChange={e => handleChange(idx, e)}
              />
               <input
                type="text"
                placeholder="Enter text"
                name="alu"
                onChange={e => handleChange(idx, e)}
              />
               <input
                type="text"
                placeholder="Enter text"
                name="siz"
                onChange={e => handleChange(idx, e)}
              />
              <button type="button" onClick={() => handleRemove(idx)}>
                X
              </button>
            </div>
          );
        })}
      </div>
    )
}

export default TransferSystemPage;
