import axios from 'axios'




export const register = newUser => {
  return axios
    .post('users/register', {
      first_name: newUser.first_name,
      last_name: newUser.last_name,
      email: newUser.email,
      password: newUser.password
    })
    .then(response => {
      console.log('Registered')
    })
}

export const login = user => {

  let url = process.env.REACT_APP_URL_BASE + "/login";
  const data = { user: user.user,password: user.password};
  return axios
    .post(url,data)
    .then(response => {
      localStorage.setItem('usertoken', response.data)
      let data = { error: 0, message: "Exito" };
      return data;
    })
    .catch(err => {
      console.log(err)
      let data = { error: 0, message: "Error, comunicarse con su administrador" };
      return data;
    })
}

export const data = () => {
  console.log(localStorage.getItem('usertoken'));
  let url = process.env.REACT_APP_URL_BASE + "/";
  let data = {token: "asd", name:"Jehiverson"};
  return axios
    .get(url,data,{
      headers: { Authorization: "Bearer " + localStorage.getItem('usertoken') }
    })
    .then(response => {
      return response;
    })
    .catch(err => {
      console.log(err)
      let data = { error: 0, message: "Error, comunicarse con su administrador" };
      return data;
    })
}
