import React, { useEffect, useState } from 'react';
import Layaout from '../../parcials/Layaout';
import CardHeader from '../../../components/CardHeader'
import { getCollaboration } from '../../../functions/collaboratorFunction';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Select from 'react-select';


import {
    MDBRow,
    MDBCol,
    MDBInput,
    MDBBtn,
    MDBIcon,
    MDBAnimation
} from 'mdbreact';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    backButton: {
        marginRight: theme.spacing(1),
    },
    instructions: {
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(1),
    },
}));

function getSteps() {
    return ['Datos de venta', 'Facturación', 'Métodos de pago'];
}

function getStepContent(stepIndex) {
    switch (stepIndex) {
        case 0:
            return(
                 <MDBRow style={{ justifyContent: "center", display: "flex" }}>
                    <MDBCol md='2'>
                        <MDBInput label='Venta diaria' type='text' validate/>
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='No. personas' type='text' validate />
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='No. Ventas' type='text' validate />
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Meta Diaria' type='text' validate />
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Venta Anterior' type='text' validate />
                    </MDBCol>
                 </MDBRow>
               
            );
        case 1:
            return (
                <>
                <MDBRow style={{ justifyContent: "center", display: "flex" }}>
                    <MDBCol md='4'>
                        Factura De Sistema
                    </MDBCol>
                </MDBRow>

                <MDBRow style={{ justifyContent: "center", display: "flex" }}>
                    <MDBCol md='2'>
                        <MDBInput label='Desde' type='text' validate/>
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Hasta' type='text' validate />
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Total' type='text' validate />
                    </MDBCol>  
                </MDBRow>

                <MDBRow style={{ justifyContent: "center", display: "flex" }}>
                    <MDBCol md='4'>
                        Factura Manual
                    </MDBCol>
                </MDBRow>
                
                <MDBRow style={{ justifyContent: "center", display: "flex" }}>
                    <MDBCol md='2'>
                        <MDBInput label='Desde' type='text' validate/>
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Hasta' type='text' validate />
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Total' type='text' validate />
                    </MDBCol>
                </MDBRow>
                
                <MDBRow style={{ justifyContent: "center", display: "flex" }}>
                    <MDBCol md='4'>
                        Factura COD
                    </MDBCol>
                </MDBRow>
                
                <MDBRow style={{ justifyContent: "center", display: "flex" }}>
                    <MDBCol md='2'>
                        <MDBInput label='Desde' type='text' validate/>
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Hasta' type='text' validate />
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Total' type='text' validate />
                    </MDBCol>       
                </MDBRow>
                
                <MDBRow style={{ justifyContent: "center", display: "flex" }}>    
                    <MDBCol md='4'>
                        Notas De Crédito
                    </MDBCol>
                </MDBRow>
                
                <MDBRow style={{ justifyContent: "center", display: "flex" }}>
                    <MDBCol md='2'>
                        <MDBInput label='Desde' type='text' validate/>
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Hasta' type='text' validate />
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Total' type='text' validate />
                    </MDBCol> 
                 </MDBRow>
                 </>
            );
        case 2:
            return 'This is the bit I really care about!';
        default:
            return 'Ooooups! Este paso no existe';
    }
}

const TransferSystemPage = () => {
    const [dataStores, setdataSores] = useState([]);

    const classes = useStyles();
    const [activeStep, setActiveStep] = React.useState(0);
    const steps = getSteps();



    const handleNext = () => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };

    const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

    const handleReset = () => {
        setActiveStep(0);
    };



    const value = { value: 'Selecciona una tienda', label: 'Selecciona una tienda' };
    return (
        <Layaout>
            <CardHeader title="Tickets" icon="ticket-alt">
            <div className={classes.root}>
                <Stepper activeStep={activeStep} alternativeLabel>
                    {steps.map((label) => (
                        <Step key={label}>
                            <StepLabel>{label}</StepLabel>
                        </Step>
                    ))}
                </Stepper>
                <div>
                    {activeStep === steps.length ? (
                        <div>
                            <Typography className={classes.instructions}>All steps completed</Typography>
                            <Button onClick={handleReset}>Reset</Button>
                        </div>
                    ) : (
                            <div>
                                <Typography className={classes.instructions} component={'span'} variant={'body2'}>{getStepContent(activeStep)}</Typography>
                                <div>
                                    <Button
                                        disabled={activeStep === 0}
                                        onClick={handleBack}
                                        className={classes.backButton}
                                    >
                                        Regresar
              </Button>
                                    <Button variant="contained" color="primary" onClick={handleNext}>
                                        {activeStep === steps.length - 1 ? 'Terminar' : 'Siguente'}
                                    </Button>
                                </div>
                            </div>
                        )}
                </div>
            </div>

            </CardHeader>
        </Layaout>
    )
}

export default TransferSystemPage;
