import axios from 'axios'

export const register = newUser => {
  return axios
    .post('users/register', {
      first_name: newUser.first_name,
      last_name: newUser.last_name,
      email: newUser.email,
      password: newUser.password
    })
    .then(response => {
      console.log('Registered')
    })
}

export const login = user => {
  return axios
    .post('users/login', {
      user: user.user,
      password: user.password
    })
    .then(response => {
      localStorage.setItem('usertoken', response.data)
      return JSON.stringify({ error: 0, message: "Exito" })
      //return response.data

    })
    .catch(err => {
      console.log(err.response.data)
      return JSON.stringify({ error: err.response.data.error, message:err.response.data.message })
      //return json({ error: err.response.data.error, message:err.response.data.error })
    })
}
