import React, { useEffect, useState } from 'react';
import Layaout from '../../parcials/Layaout';
import CardHeader from '../../../components/CardHeader'
import { getTicketsSystemTransfer, getStore } from '../../../functions/ticketFunction';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Select from 'react-select';
import {
    MDBRow,
    MDBCol,
    MDBInput,
    MDBBtn,
    MDBIcon,
    MDBAnimation
} from 'mdbreact';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    backButton: {
        marginRight: theme.spacing(1),
    },
    instructions: {
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(1),
    },
}));

function getSteps() {
    return ['Select master blaster campaign settings', 'Create an ad group', 'Create an ad'];
}

function getStepContent(stepIndex) {
    switch (stepIndex) {
        case 0:
            return (
                <>
                 <MDBRow style={{ justifyContent: "center", display: "flex" }}>
                    <MDBCol md='2'>
                        <MDBInput label='Venta Diaria' type='text' validate/>
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='No. Personas' type='number' validate/>
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='No. Ventas' type='text' validate/>
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Meta' type='text' validate/>
                    </MDBCol>
                    <MDBCol md='2'>
                        <MDBInput label='Venta Anterior' type='text' validate/>
                    </MDBCol>

                    
                </MDBRow>
                </>
            );
        case 1:
            return 'What is an ad group anyways?';
        case 2:
            return 'This is the bit I really care about!';
        default:
            return 'Unknown stepIndex';
    }
}

const TransferSystemPage = () => {
    const [dataStores, setdataSores] = useState([]);

    const classes = useStyles();
    const [activeStep, setActiveStep] = React.useState(0);
    const steps = getSteps();


    const [fields, setFields] = useState([{ upc: null, alu: null, siz: null, invoice: null, store: null }]);
    let datos = [];


    const handleNext = () => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };

    const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

    const handleReset = () => {
        setActiveStep(0);
    };

    getStore().then((res) => { _data(res) });

    let _data = (stores) => {
        stores.map(res => datos.push({ value: res.name, label: res.name }))
    }

    //created input
    function handleChange(i, event, name) {
        const values = [...fields];
        if (event.target.value == "") {
            values[i][name] = null;
        } else {
            values[i][name] = event.target.value;
        }
        setFields(values);
    }

    function handleAdd() {
        const values = [...fields];
        values.push({ upc: null, alu: null, siz: null, invoice: null, store: null });
        setFields(values);
    }

    function handleRemove(i) {
        if (i !== 0) {
            const values = [...fields];
            values.splice(i, 1);
            setFields(values);
        }
    }
    console.log(fields)

    const value = { value: 'Selecciona una tienda', label: 'Selecciona una tienda' };
    return (
        <Layaout>
            <CardHeader title="Tickets" icon="ticket-alt">
            <div className={classes.root}>
                <Stepper activeStep={activeStep} alternativeLabel>
                    {steps.map((label) => (
                        <Step key={label}>
                            <StepLabel>{label}</StepLabel>
                        </Step>
                    ))}
                </Stepper>
                <div>
                    {activeStep === steps.length ? (
                        <div>
                            <Typography className={classes.instructions}>All steps completed</Typography>
                            <Button onClick={handleReset}>Reset</Button>
                        </div>
                    ) : (
                            <div>
                                <Typography className={classes.instructions}>{getStepContent(activeStep)}</Typography>
                                <div>
                                    <Button
                                        disabled={activeStep === 0}
                                        onClick={handleBack}
                                        className={classes.backButton}
                                    >
                                        Back
              </Button>
                                    <Button variant="contained" color="primary" onClick={handleNext}>
                                        {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                                    </Button>
                                </div>
                            </div>
                        )}
                </div>
            </div>

            </CardHeader>
        </Layaout>
    )
}

export default TransferSystemPage;
