import axios from 'axios'
export const login = (user, gettoken2) => { 

  let url = process.env.REACT_APP_URL_BASE + "/login";
  const data = { user: user.user,password: user.password, gettoken: null};

  if(gettoken2 != null){
    data.gettoken = gettoken2
  }
  return axios
    .post(url,data)
    .then(response => {
      //console.log(response)
      // localStorage.setItem('usertoken', response.data);
      // localStorage.setItem('session',true);
      let data = { error: 0, message: "Exito", response: response };
      console.log(data.gettoken)
      return data;
      
    })
    .catch(err => {
      console.log(err.response.data)
      let data = { error: 1, message: err.response.data.message };
      return data;
    })
}

export const data = () => {
  
  let url = process.env.REACT_APP_URL_BASE + "/";
  let data = {};
  return axios
    .post(url,data,{
      headers: { token: localStorage.getItem('usertoken') }
    })
    .then(response => {
      return response;
    })
    .catch(err => {
      console.log(err)
      let data = { error: 0, message: "Error, comunicarse con su administrador" };
      return data;
    })
}
