import React, { Components, Component } from 'react'
import {
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBInput,
    MDBBtn,
    MDBIcon,
    MDBCard,
    MDBCardBody,
    MDBModal,
    MDBModalBody,
    MDBModalFooter
} from 'mdbreact';

//Complements
import SectionContainer from '../../components/sectionContainer';

//Css
import './Login.css'

//Functions
import { login } from '../../functions/UserFunctions'

class Login extends Component {
    constructor(){
        super();
        this.state={
            user:'',
            password:'',
            error:{}
        }
        this.onChange = this.onChange.bind(this)
        this._Login = this.onChange.bind(this)
    }

    _Login = event =>{

        console.log(this.state.user)
       
    }

    onChange(e) {
        this.setState({ user: 'Coloxo' })
        console.log([e.target.name],e.target.value)
      }

    render() {
        return (
            <MDBContainer className='mt-5'>
                <SectionContainer noBorder>
                    <MDBRow>
                        <MDBCol className="card-login" md='6'>
                            <SectionContainer>
                                <form>
                                    <p className='h5 text-center mb-4'>Iniciar Sesión</p>
                                    <div className='grey-text'>
                                        <MDBInput
                                            label='Ingrese usuario'
                                            icon='user'
                                            group
                                            type='text'
                                            validate
                                            error='wrong'
                                            success='right'
                                            name="user"
                                            onChange={this.onChange}
                                        />
                                        <MDBInput
                                            label='Ingrese Contraseña'
                                            icon='lock'
                                            group
                                            type='password'
                                            name="password"
                                            onChange={this.onChange}
                                            validate
                                        />
                                    </div>
                                    <div className='text-center'>
                                        <MDBBtn color="info" onClick={this._Login}>Ingresar</MDBBtn>
                                    </div>
                                </form>
                            </SectionContainer>
                        </MDBCol>
                        </MDBRow>
                </SectionContainer>
            </MDBContainer>
        )
    }
}

export default Login
