import React, { Components, Component } from 'react'
import { Redirect } from "react-router-dom";
import Swal from 'sweetalert2'
import {
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBInput,
    MDBBtn,
    MDBAlert
} from 'mdbreact';

//Complements
import SectionContainer from '../../components/sectionContainer';

//Css
import './Login.css'

//Functions
import { login, data } from '../../functions/UserFunctions'

class Login extends Component {
    constructor() {
        super();
        this.state = {
            user: '',
            password: '',
            error: {}
        }

    }

    componentWillMount(){
        console.log(localStorage.getItem('usertoken'));
        console.log(localStorage.getItem('session'))
    }

    _Login = () => {
        const user = {
            user: this.state.user,
            password: this.state.password
          }

        login(user).then(res => {
            
            if(res.error == 1){    
            Swal.fire('Oops...', res.message, 'error');
            }else{
            //this.props.history.push(`/landing`);
            }
        })
          
    }

    onChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    render() {
            return (
                <MDBContainer className='mt-5'>
                    <SectionContainer noBorder>
                        <MDBRow>
                            <MDBCol className="card-login" md='6'>
                                <SectionContainer>
                                    <form>
                                        <p className='h5 text-center mb-4'>Iniciar SesiÃ³n</p>
                                        <div className='grey-text'>
                                            <MDBInput
                                                label='Ingrese usuario'
                                                icon='user'
                                                group
                                                type='text'
                                                validate
                                                error='wrong'
                                                success='right'
                                                name='user'
                                                onChange={this.onChange}
                                            />
                                            <MDBInput
                                                label='Ingrese ContraseÃ±a'
                                                icon='lock'
                                                group
                                                type='password'
                                                name='password'
                                                onChange={this.onChange}
                                                validate
                                            />
                                        </div>
                                        <div className='text-center'>
                                            <MDBBtn color="info" onClick={this._Login}>Ingresar</MDBBtn>
                                        </div>
                                    </form>
                                </SectionContainer>
                            </MDBCol>
                        </MDBRow>
                    </SectionContainer>
                </MDBContainer>
            )
        
    }
}

export default Login
