import React, { Components, Component } from 'react'
import {
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBInput,
    MDBBtn,
    MDBIcon,
    MDBCard,
    MDBCardBody,
    MDBModal,
    MDBModalBody,
    MDBModalFooter
} from 'mdbreact';

//Complements
import SectionContainer from '../../components/sectionContainer';

//Css
import './Login.css'

//Functions
import { login } from '../../functions/UserFunctions'

class Login extends Component {
    constructor() {
        super();
        this.state = {
            user: '',
            password: '',
            error: {}
        }

    }

    _Login = event => {
        return axios
            .post('users/login', {
                email: user.email,
                password: user.password
            })
            .then(response => {
                localStorage.setItem('usertoken', response.data)
                return JSON.stringify({ error: 0, message: "Exito" })
                //return response.data

            })
            .catch(err => {
                console.log(err.response.data)
                return JSON.stringify({ error: err.response.data.error, message: err.response.data.message })
                //return json({ error: err.response.data.error, message:err.response.data.error })
            })
    }

    onChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    render() {
        return (
            <MDBContainer className='mt-5'>
                <SectionContainer noBorder>
                    <MDBRow>
                        <MDBCol className="card-login" md='6'>
                            <SectionContainer>
                                <form>
                                    <p className='h5 text-center mb-4'>Iniciar SesiÃ³n</p>
                                    <div className='grey-text'>
                                        <MDBInput
                                            label='Ingrese usuario'
                                            icon='user'
                                            group
                                            type='text'
                                            validate
                                            error='wrong'
                                            success='right'
                                            name="user"
                                            onChange={this.onChange}
                                        />
                                        <MDBInput
                                            label='Ingrese ContraseÃ±a'
                                            icon='lock'
                                            group
                                            type='password'
                                            name="password"
                                            onChange={this.onChange}
                                            validate
                                        />
                                    </div>
                                    <div className='text-center'>
                                        <MDBBtn color="info" onClick={this._Login}>Ingresar</MDBBtn>
                                    </div>
                                </form>
                            </SectionContainer>
                        </MDBCol>
                    </MDBRow>
                </SectionContainer>
            </MDBContainer>
        )
    }
}

export default Login
